DROP INDEX [IX_Ticket_SupportId] ON [Ticket];
DECLARE @var0 sysname;
SELECT @var0 = [d].[name]
FROM [sys].[default_constraints] [d]
INNER JOIN [sys].[columns] [c] ON [d].[parent_column_id] = [c].[column_id] AND [d].[parent_object_id] = [c].[object_id]
WHERE ([d].[parent_object_id] = OBJECT_ID(N'[Ticket]') AND [c].[name] = N'SupportId');
IF @var0 IS NOT NULL EXEC(N'ALTER TABLE [Ticket] DROP CONSTRAINT [' + @var0 + '];');
UPDATE [Ticket] SET [SupportId] = '00000000-0000-0000-0000-000000000000' WHERE [SupportId] IS NULL;
ALTER TABLE [Ticket] ALTER COLUMN [SupportId] uniqueidentifier NOT NULL;
ALTER TABLE [Ticket] ADD DEFAULT '00000000-0000-0000-0000-000000000000' FOR [SupportId];
